import { API_URL, RES_PER_PAGE,KEY } from './config'
import { getJSON, getLocalStorage ,sendJSON } from './helpers'
export const state = {
    recipe: {},
    search: {
        query: '',
        results: [],
        page: 1,
        resultsPerPage: RES_PER_PAGE
    },
    bookmarks: []

}

const createRecipeObject = function (recipe) {
 
    return {
        id: recipe.id,
        title: recipe.title,
        ingredients: recipe.ingredients,
        publisher: recipe.publisher,
        sourceUrl: recipe.source_url,
        image: recipe.image_url,
        servings: recipe.servings,
        cookingTime: recipe.cooking_time,
        ...(recipe.key && {key:recipe.key})

    }
}


export const loadRecipe = async function (id) {
    //Loading the Recipe
    try {

        const { data: { recipe } } = await getJSON(`${API_URL}/${id}`)
        
        state.recipe = createRecipeObject(recipe)
         

        if (state.bookmarks.some(bookmark => bookmark.id === id)) {
            state.recipe.bookmarked = true
        } else {
            state.recipe.bookmarked = false
        }
    } catch (error) {
        throw error;
    }
}

export const loadSearchResults = async function (query) {
    try {
        state.search.query = query;

        const data = await getJSON(`${API_URL}?search=${query}`)

        state.search.results = data.data.recipes.map(rec => {
            return {
                id: rec.id,
                title: rec.title,
                publisher: rec.publisher,
                image: rec.image_url,
            }
        })

        state.bookmarks = getLocalStorage()

    } catch (error) {
        throw error;
    }
}

export const getSearchResultsPage = function (page = 1) {
    state.search.page = page;
    const start = (page - 1) * state.search.resultsPerPage;
    const end = page * state.search.resultsPerPage;

    return state.search.results.slice(start, end);
}

export const updateServings = function (newServings) {

    state.recipe.ingredients.forEach(ing => {
        ing.quantity = (ing.quantity * newServings) / state.recipe.servings
    });
    state.recipe.servings = newServings
}


export const addBookMark = function (id) {
 
    if (state.recipe.id === id) state.recipe.bookmarked = true

    state.bookmarks.push(state.recipe)

    localStorage.setItem("bookmarks", JSON.stringify(state.bookmarks))

}

export const deleteBookMark = function (id) {
    if (state.recipe.id === id) state.recipe.bookmarked = false

    const newBookmarks = state.bookmarks.filter(bookmark => bookmark.id !== id)

    state.bookmarks = newBookmarks

    localStorage.setItem("bookmarks", JSON.stringify(state.bookmarks))
}


export const uploadRecipe = async function (newRecipe) {
    try {
        const ingredients = Object.entries(newRecipe)
            .filter(entry =>
                entry[0].includes("ingredient")
                && entry[1] !== ''
            ).map(ing => {
                const ingArr = ing[1].replaceAll(" ", "").split(",")
                if (ingArr.length !== 3) {
                    throw new Error("Wrong Ingredient format. Please use the correct format.")
                }

                const [quantity, unit, description] = ingArr

                return {
                    quantity: quantity ? Number(quantity) : null,
                    unit,
                    description
                }
            })
        const recipe = {
            title: newRecipe.title,
            source_url: newRecipe.sourceUrl,
            image_url: newRecipe.image,
            publisher:newRecipe.publisher,
            cooking_time: Number(newRecipe.cookingTime),
            servings: Number(newRecipe.servings),
            ingredients,
        }

        const { data:{ recipe:recipeData } } = await sendJSON(`${API_URL}?key=${KEY}`,recipe)
        
        state.recipe = createRecipeObject(recipeData)
        addBookMark(state.recipe)
    } catch (error) {
        throw error
    }
}


function init() {
    state.bookmarks = getLocalStorage()
}
init()
